// src/pages/Landingpage.js
import React from "react";
import Header from "../components/Header";
import Slider from "react-slick";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";

const dummyPlots = [
  {
    id: 1,
    title: "Insight 1",
    image: "/images/plot1.png",
    description: "Exploring average duration vs popularity.",
  },
  {
    id: 2,
    title: "Insight 2",
    image: "/images/plot2.png",
    description: "Distribution of track popularity.",
  },
  {
    id: 3,
    title: "Insight 3",
    image: "/images/plot3.png",
    description: "Trends across release years.",
  },
  {
    id: 4,
    title: "Insight 4",
    image: "/images/plot4.png",
    description: "Danceability vs Energy patterns.",
  },
];

const Landingpage = () => {
  const settings = {
    dots: true,
    infinite: true,
    speed: 600,
    slidesToShow: 1,
    slidesToScroll: 1,
    autoplay: true,
    autoplaySpeed: 3500,
  };

  return (
    <>
      <Header />
      <div className="pt-10 px-4">
        <h1 className="text-3xl font-bold text-center mb-6">
          Discover the Power of Data in Music
        </h1>
        <p className="text-center text-gray-700 mb-12 max-w-2xl mx-auto">
          EchoTrends offers a dynamic exploration of your Spotify playlists.
          Even without logging in, you can get a glimpse of how we turn raw
          track data into meaningful insightsâ€”visually and interactively.
        </p>

        <div className="max-w-3xl mx-auto">
          <Slider {...settings}>
            {dummyPlots.map((plot) => (
              <div key={plot.id} className="px-4">
                <div className="bg-white shadow-lg rounded-lg overflow-hidden">
                  <img
                    src={plot.image}
                    alt={plot.title}
                    className="w-full h-80 object-contain"
                  />
                  <div className="p-4 text-center">
                    <h2 className="text-xl font-semibold">{plot.title}</h2>
                    <p className="text-sm text-gray-600 mt-2">
                      {plot.description}
                    </p>
                  </div>
                </div>
              </div>
            ))}
          </Slider>
        </div>
      </div>
    </>
  );
};

export default Landingpage;
